namespace Poweradmin\Domain\Service\DnsValidation;

use Poweradmin\Infrastructure\Configuration\ConfigurationManager;
use Poweradmin\Infrastructure\Service\MessageService;

/**
 * SPF record validator
 *
 * @package Poweradmin
 * @copyright   2007-2010 Rejo Zenger <rejo@zenger.nl>
 * @copyright   2010-2025 Poweradmin Development Team
 * @license     https://opensource.org/licenses/GPL-3.0 GPL
 */
class SPFRecordValidator implements DnsRecordValidatorInterface
{
    private ConfigurationManager $config;
    private MessageService $messageService;
    private HostnameValidator $hostnameValidator;
    private TTLValidator $ttlValidator;

    public function __construct(ConfigurationManager $config)
    {
        $this->config = $config;
        $this->messageService = new MessageService();
        $this->hostnameValidator = new HostnameValidator($config);
        $this->ttlValidator = new TTLValidator();
    }

    /**
     * Validates SPF record content
     *
     * @param string $content The content of the SPF record
     * @param string $name The name of the record
     * @param mixed $prio The priority (unused for SPF records)
     * @param int|string $ttl The TTL value
     * @param int $defaultTTL The default TTL to use if not specified
     *
     * @return array|bool Array with validated data or false if validation fails
     */
    public function validate(string $content, string $name, mixed $prio, $ttl, $defaultTTL): array|bool
    {
        // Validate hostname
        $hostnameResult = $this->hostnameValidator->isValidHostnameFqdn($name, 1);
        if ($hostnameResult === false) {
            return false;
        }
        $name = $hostnameResult['hostname'];

        // Validate SPF content and ensure proper quoting
        if (!StringValidator::hasQuotesAround($content)) {
            $content = '"' . $content . '"';
        }

        // Remove quotes for validation
        $unquotedContent = trim($content, '"');
        if (!$this->isValidSPFContent($unquotedContent)) {
            $this->messageService->addSystemError(_('The content of the SPF record is invalid'));
            return false;
        }

        // Validate TTL
        $validatedTTL = $this->ttlValidator->isValidTTL($ttl, $defaultTTL);
        if ($validatedTTL === false) {
            return false;
        }

        return [
            'content' => $content,
            'name' => $name,
            'prio' => 0, // SPF records don't use priority
            'ttl' => $validatedTTL
        ];
    }

    /**
     * Validate SPF record content format
     *
     * @param string $content SPF record content
     *
     * @return bool True if valid, false otherwise
     */
    private function isValidSPFContent(string $content): bool
    {
